import React, { useContext, useState } from 'react'
import { data } from '../Context/StatesContext';

const Filter = () => {
    const {
       Mens,
       setMens,
       Electronics,
       setElectronics,
       Jewellery,
       setJewellery
      } = useContext(data);
    const [isVisible, setIsVisible] = useState(false);
    // console.log(Mens);
    const toggleForm = () => {
      setIsVisible(!isVisible);
    };
  return (
    <>
        <img style={{aspectRatio:"3/4",width:"10%",mixBlendMode:"darken",filter:"brightness(0.5)"}} src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSOhuy_XTB3n4ZjpKxU-hWI8wNowxh9m8W4fA&s" alt="" onClick={toggleForm} />

        <div className={`sliding-form ${isVisible ? 'visible' : ''}`}>
        <label className="checkbox-container">
          <input type="checkbox" value={Mens}  onClick={()=>setMens(!Mens)} />
          <span className="checkmark"></span>
          Men's Clothing
        </label>
        <label className="checkbox-container">
          <input type="checkbox"  value={Electronics}  onClick={()=>setElectronics(!Electronics)} />
          <span className="checkmark"></span>
          Electronics
        </label>
        <label className="checkbox-container">
          <input type="checkbox" value={Jewellery} onClick={()=>setJewellery(!Jewellery)} />
          <span className="checkmark"></span>
          Jewellery
        </label>
      </div>
    </>
  )
}

export default Filter
